 var current;
 var probe_buffer = new ArrayBuffer(buffer_size);
 var prime_buffer = new ArrayBuffer(buffer_size);
 var probe_view = new DataView(probe_buffer);
 var prime_view = new DataView(prime_buffer);
 var iteration = 100;
 var x = 4;
var toto;
 function probe()
 {
   var currentTime = window.performance.now();
 for (var j = 0; j < ((buffer_size) / line_length); j++) {
   current = probe_view.getUint32(j * line_length);
 }
  //while (window.performance.now() < currentTime + 0.12);
 //sleep(1);
 }

 function prime()
 {
  var currentTime = window.performance.now();
    for (var j = 0; j < ((buffer_size) / line_length); j++) {
     current = probe_view.getUint32(x);
   }
  //  while (window.performance.now() < currentTime + 0.12);

   }


   function sleep(delay) {
   	var start = window.performance.now();
   	while (window.performance.now() < start + delay);
   }


   function envoyerBits(tableaus)
   {
     for(var i = 0; i < tableaus.length;i++)
     {
       if(tableaus[i] === 0 )
       {
         probe();
         console.log('plouf');
       }
       else {

           prime();
              console.log('prime');

       }
     }
     console.log("taille tableau dans envoyer = " + tableaus.length);
   }
   function envoyerEnAlternance(nombre)
   {
     for(var i = 0; i < nombre;i++)
     {
       if(i % 2 === 0)
       {

         	var startTime2 = window.performance.now();
         probe();
         	var diffTime2 = window.performance.now() - startTime2;
          console.log("temps pour probe = " + diffTime2);

       }
       else
         {

                  var startTime2 = window.performance.now();

                    prime();
	var diffTime2 = window.performance.now() - startTime2;
 console.log("temps pour prime = " + diffTime2);

         }

     }
   }
